import React, { Component } from "react";
import "../login.css";

class Login extends Component {
  state = { adminInfo: { username: "", password: "" } };



  handleChangeContact = e => {
    // dont update the state directly clone the state then let react update the state
    const adminInfo = { ...this.state.adminInfo };
    contactInfo[e.currentTarget.name] = e.currentTarget.value;
    this.setState({ contactInfo });
  };


  render() {
    const { adminInfo } = this.state;
    return (
      <main>
        <div clasclassNames="login-page">
          <div className="form">
            {/* <form className="register-form">
              <input type="text" placeholder="name" />
              <input type="password" placeholder="password" />
              <input type="text" placeholder="email address" />
              <button>create</button>
              <p className="message">
                <a href="#"></a>
              </p>
            </form> */}
            <form className="login-form">
              <input
                autoFocus
                type="text"
                placeholder="username"
                value={adminInfo.username}
                onChange={this.handleChangeContact}
              />
              <input
                type="password"
                placeholder="password"
                value={adminInfo.password}
                onChange={this.handleChangeContact}
              />
              <button>login</button>
              <p className="message">
                <a href="#"></a>
              </p>
            </form>
          </div>
        </div>
      </main>
    );
  }
}

export default Login;
